const { memdb } = require('helpers/modules/memdb');

function userisAllowed(user, command) {
    if (!memdb.cache['blacklist']) memdb.read('blacklist');

    if (memdb.cache['blacklist'][user]) {
        if (memdb.cache['blacklist'][user]['banned-commands'].includes(command)
        ||  memdb.cache['blacklist'][user]['banned-commands'].includes("all"))
        return false;
    }

    return true;
}

module.exports = { userisAllowed }