const { memdb } = require('helpers/modules/memdb');

function verifyMessage(message) {
    if (!memdb.cache['special-messages']) {
        memdb.read('special-messages');
        memdb.hash.map('message', 'special-messages');
    }

    const index = memdb.hash.find(message, 'special-messages');

    return memdb.cache['special-messages'][index] ? memdb.cache['special-messages'][index] : null;
};

module.exports = {
    specialMsgVerifier: verifyMessage
}