const { specialMsgVerifier } = require('helpers/@messages/verifiers/special-message.js');
const { verifiyID } = require('helpers/@messages/special/verify-id.js');

const checkAndReturnSpecialMsg = async (message) => {
    const channel = message.client.channels.cache.get(message.channelId);
    const specialMessage = specialMsgVerifier(message.content);

    if (specialMessage != null && verifiyID(message.author.id, specialMessage)) {
        try {
            channel.send(specialMessage.reply);
        } catch (error) {
            console.error(error);
        }
    }
};

module.exports = checkAndReturnSpecialMsg;